package com.kirti.string;import java.util.Arrays;public class ArrayToString    {        public static void main(String[] args)        {            String[] strArray = new String[]{"Java", "PHP", ".NET", "PERL", "C", "COBOL"};        String newString = Arrays.toString(strArray);        newString = newString.substring(1, newString.length()-1);        System.out.println("New New String: " + newString);    }}